import React, {Component} from 'react';
import Particles from 'react-particles-js';
import './App.css'
import Navigation from './components/Navigation/Navigation';
import FaceRecognition from './components/FaceRecognition/FaceRecognition'
import Clarifai from 'clarifai';
import Rank from './components/Rank/Rank';
import Logo from './components/Logo/Logo';
import InputImage from './components/InputImage/InputImage';

const app = new Clarifai.App({
 apiKey: 'da7aaec4862d461a9cbeb68de14ed14c'
});

const properties = {
	"particles": {
    "number": {
      "value": 20,
      "density": {
        "enable": true,
        "value_area": 90
      }
    }
   }  
};

class App extends Component {
  constructor(){
    super()
    this.state = {
      'input' : '',
      'imgUrl': ''
    }
  }

  onSearchChange = (event) => {
     this.setState({'input' : event.target.value})
  };

  onSubmit = () => {
    this.setState({'imgUrl': this.state.input})
    app.models.predict(Clarifai.FACE_DETECT_MODEL
      ,'https://st.depositphotos.com/1008939/1880/i/600/depositphotos_18807295-stock-photo-portrait-of-handsome-man.jpg').then(
      function(response){
        console.log(response);
      },
      function(err){
        console.log(err);
      })
  };

  render(){
    return(
    <div>
    <Particles className='particles'
      params={properties} />
    <Navigation />
    <Logo />
    <Rank />
    <InputImage  searchChange ={this.onSearchChange} submit = {this.onSubmit}/>
    <FaceRecognition image = {this.state.imgUrl}/>
    </div>
    );
  };
};

export default App;
