import React from 'react';

class SignUp extends React.Component{

  onEmailChange = (event) => {
    this.setState({signInEmail : event.target.value})
  }

  onPasswordChange = (event) => {
    this.setState({signInPassword : event.target.value})
  }



  render(){
  const {currentRoute} = this.props
	return(
   <article className="center shadow-5 br4 ba dark-gray b--black-10 mv6 w-100 w-50-m w-25-l mw7">
    <main className="pa2 black-80">
      <form className="measure">
       <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
         <legend className="center f3 fw6 ph0 mh0">Sign In</legend>
        <div className="mt1">
         <label className="db fw6 lh-copy f4" htmlFor="email-address">Email</label>
         <input className="f3 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address"/>
        </div>
        <div className="mv2">
         <label className="db fw6 lh-copy f4" htmlFor="password">Password</label>
         <input className="f3 b input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password"/>
        </div>
      </fieldset>
      <div className="center">
       <input 
       onClick = {() => currentRoute('home')}
       className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f5 dib" 
       type="submit" 
       value="Sign in"/>
      </div>
      <div className="center lh-copy">
        <p 
        onClick = {() => currentRoute('register')}
        className="pointer mt2 f6 link dim black db">Register</p>
      </div>
      </form>
   </main>
  </article> 
		);
  };
};

export default SignUp;
