import React, {Component} from 'react';
import Particles from 'react-particles-js';
import './App.css'
import Navigation from './components/Navigation/Navigation';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import SignUp from './components/SignUp/SignUp';
import Register from './components/Register/Register';
import Clarifai from 'clarifai';
import Rank from './components/Rank/Rank';
import Logo from './components/Logo/Logo';
import InputImage from './components/InputImage/InputImage';

const app = new Clarifai.App({
 apiKey: 'da7aaec4862d461a9cbeb68de14ed14c'
});

const properties = {
	"particles": {
    "number": {
      "value": 15,
      "density": {
        "enable": true,
        "value_area": 90
      }
    }
   }  
};

class App extends Component {
  constructor(){
    super()
    this.state = {
      input : '',
      imgUrl: '',
      box : {},
      route : false,
      loggedIn : false
    }
  }

  calculateFaceLocation = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width-(clarifaiFace.right_col * width),
      bottomRow: height-(clarifaiFace.bottom_row * height)  
    }
  };

  currentRoute = (route) => {
    if(this.loggedIn == false){
    this.setState({route : false})
    } else {
     this.setState({route:'home'})
    }
  }


  displayBox = (box) => {
    this.setState({box: box});
  }

  onSearchChange = (event) => {
     this.setState({input:event.target.value})
  };

  onSubmit = () => {
    this.setState({imgUrl: this.state.input})
    app.models.predict(
      Clarifai.FACE_DETECT_MODEL,
      this.state.input)
    .then(response => this.displayBox(this.calculateFaceLocation(response)))
       .catch(err => console.log(err))
  };

  render(){
    return(
    <div>
    <Particles className='particles'
      params={properties} />
    { this.state.route === false 
    ? <SignUp currentRoute = {this.currentRoute}/>
    : ( this.state.route === 'register' ? <Register currentRoute = {this.currentRoute} />
      :
      <div>
       <Navigation currentRoute={this.currentRoute}/>
       <Logo />
       <Rank />
       <InputImage searchChange ={this.onSearchChange} submit = {this.onSubmit}/>
       <FaceRecognition box = {this.state.box} image = {this.state.imgUrl}/>
      </div>)
    }
    </div>
    );
  };
};

export default App;
